'use strict';
angular.module('mean.events').controller('EventemmitterController', ['$scope', 'Global', 'Events',
	function($scope, Global, Eventemmitter, Events) {
		$scope.global = Global;
		$scope.eventemmitter = {name:'eventemmitter'};

        $scope.find = function() {
            Events.query(function(events) {
                $scope.events = events;
            });
        };

	}
]);