'use strict';

// The Package is past automatically as first parameter
module.exports = function(Eventemmitter, app, auth, database) {

    app.get('/eventemmitter/example/anyone', function (req,res,next) {
    	res.send('Anyone can access this');
    });

    app.get('/eventemmitter/example/auth',auth.requiresLogin, function (req,res,next) {
    	res.send('Only authenticated users can access this');
    });

    app.get('/eventemmitter/example/admin',auth.requiresAdmin, function (req,res,next) {
    	res.send('Only users with Admin role can access this');
    });    

    app.get('/eventemmitter/example/render', function (req,res,next) {
    	Eventemmitter.render('index', {package:'eventemmitter'}, function (err, html) {
    		//Rendering a view from the Package server/views
    		res.send(html);
    	})
    })
};